this.screenshotPreview=function(){xOffset=9;yOffset=9;$("a.screenshot").hover(function(e){this.t=this.title;this.title="";var t=this.t!=""?"<br/>"+this.t:"";$("body").append("<p id='screenshot'><img src='"+this.rel+"' alt='Article preview' width='700px'/>"+t+"</p>");$("#screenshot").css("top",e.pageY-xOffset+"px").css("left",e.pageX+yOffset+"px").fadeIn("fast")},function(){this.title=this.t;$("#screenshot").remove()});$("a.screenshot").mousemove(function(e){$("#screenshot").css("top",e.pageY-xOffset+"px").css("left",e.pageX+yOffset+"px")})};$(document).ready(function(){screenshotPreview()})